{
  "tools": {
    "claude-code": {
      "hooks": {
        "UserPromptSubmit": {
          "command": "uv run ~/.claude/hooks/user_prompt_submit.py",
          "timeout": 10000,
          "enabled": true
        },
        "PreToolUse": {
          "command": "uv run ~/.claude/hooks/pre_tool_use.py",
          "timeout": 10000,
          "enabled": true
        },
        "PostToolUse": {
          "command": "uv run ~/.claude/hooks/post_tool_use.py",
          "timeout": 10000,
          "enabled": true
        },
        "SessionStart": {
          "command": "uv run ~/.claude/hooks/session_start.py --git-status --verbose",
          "timeout": 10000,
          "enabled": true
        },
        "PreCompact": {
          "command": "uv run ~/.claude/hooks/pre_compact.py --handover --backup --verbose",
          "timeout": 30000,
          "enabled": true
        },
        "Notification": {
          "command": "uv run ~/.claude/hooks/notification.py --notify",
          "timeout": 10000,
          "enabled": true
        },
        "Stop": {
          "command": "uv run ~/.claude/hooks/stop.py --chat",
          "timeout": 10000,
          "enabled": true
        }
      }
    }
  }
}
