#!/usr/bin/env python3

import os
import subprocess
from pathlib import Path

def debug_tts_selection():
    """Debug which TTS script would be selected"""
    script_dir = Path.home() / ".claude" / "hooks"
    tts_dir = script_dir / "utils" / "tts"

    print("🔍 TTS Debug Information")
    print("=" * 30)
    print(f"Script directory: {script_dir}")
    print(f"TTS directory: {tts_dir}")
    print(f"TTS directory exists: {tts_dir.exists()}")

    if tts_dir.exists():
        print(f"Contents: {list(tts_dir.iterdir())}")

    print()
    print("API Key Check:")
    print(f"ELEVENLABS_API_KEY: {'✅ Set' if os.getenv('ELEVENLABS_API_KEY') else '❌ Not set'}")
    print(f"OPENAI_API_KEY: {'✅ Set' if os.getenv('OPENAI_API_KEY') else '❌ Not set'}")

    print()
    print("TTS Script Selection:")

    # Check for ElevenLabs
    if os.getenv('ELEVENLABS_API_KEY'):
        elevenlabs_script = tts_dir / "elevenlabs_tts.py"
        if elevenlabs_script.exists():
            print(f"🎯 Selected: ElevenLabs TTS ({elevenlabs_script})")
            return str(elevenlabs_script)

    # Check for OpenAI
    if os.getenv('OPENAI_API_KEY'):
        openai_script = tts_dir / "openai_tts.py"
        if openai_script.exists():
            print(f"🎯 Selected: OpenAI TTS ({openai_script})")
            return str(openai_script)

    # Check for macOS say
    macos_say_script = tts_dir / "macos_say_tts.py"
    if macos_say_script.exists():
        print(f"🎯 Selected: macOS Say TTS ({macos_say_script})")
        return str(macos_say_script)

    # Check for pyttsx3
    pyttsx3_script = tts_dir / "pyttsx3_tts.py"
    if pyttsx3_script.exists():
        print(f"🎯 Selected: pyttsx3 TTS ({pyttsx3_script})")
        return str(pyttsx3_script)

    print("❌ No TTS script found")
    return None

def test_selected_tts(script_path):
    """Test the selected TTS script"""
    if not script_path:
        return

    print()
    print("🧪 Testing Selected TTS Script:")
    print(f"Running: python {script_path} 'TTS Debug Test'")

    try:
        result = subprocess.run([
            'python', script_path, 'TTS Debug Test Message'
        ], capture_output=True, text=True, timeout=15)

        print(f"Return code: {result.returncode}")
        print(f"Stdout: {result.stdout}")
        if result.stderr:
            print(f"Stderr: {result.stderr}")

    except Exception as e:
        print(f"Error running TTS: {e}")

if __name__ == "__main__":
    selected_script = debug_tts_selection()
    test_selected_tts(selected_script)

